declare class clipboard {
    static copy(): Promise<string>;
    static setString(text: string): void;
}

interface BridgeRequest {
    text: string;
    context: {};
    options: {};
}
declare class req {
    static body(): BridgeRequest;
}

declare class res {
    static text(content: string): Promise<any>;
    static error(message: string): Promise<any>;
    static respond: string;
    static end(): Promise<any>;
    static write(content: string, type?: string): Promise<any>;
}

declare class language {
    static detect(text: string): Promise<string>;
    static isWord(langCode: string, text: string): boolean;
    static splitByUpperCase(str: string): string[];
    static splitWord(str: string): string;
}

declare class ocr {
    static screenShotOcr(): Promise<string>;
}

declare class display {
    static showMainWindow(key: string, options: {}): void;
}

export { clipboard, display, language, ocr, req, res };
