export default function anthropicStreamValue(value) {
    const lines = value.split('\n'); // 将字符串按照换行符分割
    const result = {};

    lines.forEach(line => {
        var parts = line.split(': ') // 将每一行按照': '分割
        if (parts.length === 2) {
            var key = parts[0]
            var value = parts[1]

            // 如果 key 是 'data'，则将 value 转换成一个对象
            if (key === 'data') {
                value = JSON.parse(value)
            }

            result[key] = value
        }
    })

    return result
}

